mapper.update:
/* CartMapper.updateCartAdd */
UPDATE CART_MST SET QTY = QTY + 1
WHERE 1=1


		AND USER_SESSION = #{userSession}	


		AND USER_NO = #{userNo}


AND MENU_BOARD_SEQ = #{menuBoardSeq}

mapper.select:
/* CartMapper.selectCartList */ SELECT A.CART_NO , A.USER_NO , A.USER_SESSION , A.MENU_BOARD_SEQ , A.ORDER_SET , A.QTY , A.REG_USER , DATE_FORMAT(A.REG_DT,'%Y-%m-%d %H:%i:%s') AS REG_DT , CONCAT(DATE_FORMAT(B.MENU_DAY,'%Y-%m-%d'),' (', CASE DAYOFWEEK(B.MENU_DAY) WHEN '2' THEN '월' WHEN '3' THEN '화' WHEN '4' THEN '수' WHEN '5' THEN '목' WHEN '6' THEN '금' END ,')') MENU_DAY , B.MENU_MSG FROM CART_MST A INNER JOIN MENU_BOARD B on A.MENU_BOARD_SEQ = B.MENU_BOARD_SEQ WHERE 1=1
AND USER_SESSION = #{userSession}
AND USER_NO = #{userNo}
AND A.MENU_BOARD_SEQ = #{menuBoardSeq}
ORDER BY B.MENU_DAY

/* CartMapper.selectCartDetail */ SELECT A.CART_NO , A.USER_NO , A.USER_SESSION , A.MENU_BOARD_SEQ , A.QTY , A.REG_USER , DATE_FORMAT(A.REG_DT,'%Y-%m-%d %H:%i:%s') AS REG_DT , CONCAT(DATE_FORMAT(B.MENU_DAY,'%Y-%m-%d'),' (', CASE DAYOFWEEK(B.MENU_DAY) WHEN '2' THEN '월' WHEN '3' THEN '화' WHEN '4' THEN '수' WHEN '5' THEN '목' WHEN '6' THEN '금' END ,')') MENU_DAY , B.MENU_MSG FROM CART_MST A INNER JOIN MENU_BOARD B on A.MENU_BOARD_SEQ = B.MENU_BOARD_SEQ WHERE 1=1
AND USER_SESSION = #{userSession}
AND USER_NO = #{userNo}
AND A.MENU_BOARD_SEQ = #{menuBoardSeq}
AND CART_NO = #{cartNo}

mapper.update:
/* CartMapper.updateCartQty */
UPDATE CART_MST SET


		QTY = QTY + 1


		QTY = QTY - 1


WHERE CART_NO = #{cartNo}

/* CartMapper.updateCartOrderSet */
UPDATE CART_MST SET
ORDER_SET = #{orderSet}
WHERE CART_NO = #{cartNo}

mapper.delete:
/* CartMapper.deleteCart */
DELETE FROM CART_MST
WHERE CART_NO IN

	#{item}

