mapper.update:
/* CartMapper.updateCartAdd */
UPDATE CART_MST SET QTY = QTY + 1, MENU_MSG_DETAIL = CONCAT_WS('|', QTY, QTY , QTY , QTY , QTY ) 
WHERE 1=1


		AND USER_SESSION = #{userSession}	


		AND USER_NO = #{userNo}


AND MENU_BOARD_SEQ = #{menuBoardSeq}

mapper.select:
/* CartMapper.selectCartList */ SELECT A.CART_NO , A.USER_NO , A.USER_SESSION , A.MENU_BOARD_SEQ , A.MENU_MSG_DETAIL , A.ORDER_SET , A.DELIV_TIME , A.QTY , A.REG_USER , DATE_FORMAT(A.REG_DT,'%Y-%m-%d %H:%i:%s') AS REG_DT , CONCAT(DATE_FORMAT(B.MENU_DAY,'%Y-%m-%d'),' (', CASE DAYOFWEEK(B.MENU_DAY) WHEN '2' THEN '월' WHEN '3' THEN '화' WHEN '4' THEN '수' WHEN '5' THEN '목' WHEN '6' THEN '금' END ,')') MENU_DAY , B.MENU_MSG FROM CART_MST A INNER JOIN MENU_BOARD B ON A.MENU_BOARD_SEQ = B.MENU_BOARD_SEQ WHERE 1=1
AND USER_SESSION = #{userSession}
AND USER_NO = #{userNo}
AND A.MENU_BOARD_SEQ = #{menuBoardSeq}
ORDER BY B.MENU_DAY

/* CartMapper.selectCartDetail */ SELECT A.CART_NO , A.USER_NO , A.USER_SESSION , A.MENU_BOARD_SEQ , A.MENU_MSG_DETAIL , A.ORDER_SET , A.DELIV_TIME , A.QTY , A.REG_USER , DATE_FORMAT(A.REG_DT,'%Y-%m-%d %H:%i:%s') AS REG_DT , CONCAT(DATE_FORMAT(B.MENU_DAY,'%Y-%m-%d'),' (', CASE DAYOFWEEK(B.MENU_DAY) WHEN '2' THEN '월' WHEN '3' THEN '화' WHEN '4' THEN '수' WHEN '5' THEN '목' WHEN '6' THEN '금' END ,')') MENU_DAY , B.MENU_MSG FROM CART_MST A INNER JOIN MENU_BOARD B ON A.MENU_BOARD_SEQ = B.MENU_BOARD_SEQ WHERE 1=1
AND USER_SESSION = #{userSession}
AND USER_NO = #{userNo}
AND A.MENU_BOARD_SEQ = #{menuBoardSeq}
AND CART_NO = #{cartNo}

mapper.update:
/* CartMapper.updateCartQty */
UPDATE CART_MST SET



		MENU_MSG_DETAIL = CONCAT_WS('|', QTY + 1, QTY + 1, QTY + 1, QTY + 1, QTY + 1),
		QTY = QTY + 1
		WHERE CART_NO = #{cartNo} 
		AND QTY > 0


		MENU_MSG_DETAIL = CONCAT_WS('|', QTY - 1, QTY - 1, QTY - 1, QTY - 1, QTY - 1),
		QTY = QTY - 1
		WHERE CART_NO = #{cartNo} 
		AND QTY > 1

/* CartMapper.updateCartMenuMsgQty */
UPDATE CART_MST SET
MENU_MSG_DETAIL = #{menuMsgDetail}
WHERE CART_NO = #{cartNo}
AND ORDER_SET = '1000'

/* CartMapper.updateCartOrderSet */
UPDATE CART_MST SET
ORDER_SET = #{orderSet}
WHERE CART_NO = #{cartNo}

/* CartMapper.updateCartOrderDelivTime */
UPDATE CART_MST SET
DELIV_TIME = #{delivTime}
WHERE CART_NO = #{cartNo}

mapper.delete:
/* CartMapper.deleteCart */
DELETE FROM CART_MST
WHERE CART_NO IN

	#{item}

