mapper.insert:
/* MemberMapper.insertMember */
INSERT INTO USER_MST
(USER_ID, USER_PW, USER_NM, USER_EMAIL, USER_MOBILE, ZIPCODE, ADDRESS, ADDRESS_DETAIL, LAST_LOGIN_DT, REG_USER, REG_DT)
VALUES(
	  #{userId}
	, #{userPw}
	, #{userNm}
	, #{userEmail}
	, #{userMobile}
	, #{zipcode}
	, #{address}
	, #{addressDetail}
	, SYSDATE()
	, #{userId}
	, SYSDATE()
);

mapper.select:
/* MemberMapper.selectMemberList */ SELECT A.USER_NO , A.USER_ID , A.USER_PW , A.USER_NM , A.USER_EMAIL , A.USER_TEL , A.USER_MOBILE , A.ZIPCODE , A.ADDRESS , A.ADDRESS_DETAIL , A.PW_CHANGE_DT , A.LAST_LOGIN_DT , A.REG_USER , DATE_FORMAT(A.REG_DT,'%Y-%m-%d %H:%i:%s') AS REG_DT , A.UPT_USER , DATE_FORMAT(A.UPT_DT,'%Y-%m-%d %H:%i:%s') AS UPT_DT FROM USER_MST A WHERE 1=1
AND USER_NO = #{userNo}
AND USER_STATUS = '1000'

/* MemberMapper.selectIdCheck */
SELECT 
	  COUNT(*) AS TOT_CNT
FROM USER_MST
WHERE USER_ID = #{userId}
AND USER_STATUS = '1000'
