mapper.insert:
/* OrderMapper.insertOrderMst */

	SELECT FN_CREATE_ORDER_NO() AS ORDER_NO

INSERT INTO ORDER_MST
	(
			ORDER_NO
		,	USER_NO
		,	ORDER_STATUS
		,	USER_SESSION
		,	ORDER_PAY_TYPE
		,	TOTAL_PRODUCT_AMT
		,	TOTAL_DIS_AMT
		,	TOTAL_DELY_AMT
		,	MARKETING_AGREE_FG
		,	REG_USER
		,	REG_DT
	)
VALUES(
			#{orderNo}
		,	#{userNo}
		,	'1000'
		,	#{userSession}
		,	'1'
		,	#{totalProductAmt}
		,	#{totalDisAmt}
		,	#{totalDelyAmt}
		,	'Y'
		,	#{userNo}
		,	NOW()
)

/* OrderMapper.insertOrderProduct */
INSERT INTO ORDER_PRODUCT(ORDER_NO, ORDER_QTY, MENU_BOARD_SEQ, ORDER_SET, MENU_DAY, MENU_MSG, PAY_AMT, DELIVERY_TYPE, USED_POINT, REG_USER, REG_DT)

SELECT	  #{orderNo}
		, #{orderQty}
		, A.MENU_BOARD_SEQ
		, #{orderSet}
		, A.MENU_DAY
		, A.MENU_MSG
		, IFNULL(#{payAmt}, 0)
		, 'Y'
		, IFNULL(#{usedPoint}, 0)
		, #{regUser}
		, NOW()
FROM MENU_BOARD A
WHERE A.MENU_BOARD_SEQ = #{menuBoardSeq}

	SELECT LAST_INSERT_ID()

mapper.select:
/* OrderMapper.selectOrderApplyInfo */ SELECT ORDER_NO , USER_NO , ORDER_STATUS , USER_SESSION , ORDER_PAY_TYPE , TOTAL_PRODUCT_AMT , TOTAL_DIS_AMT , TOTAL_DELY_AMT , MARKETING_AGREE_FG , ORDER_SET , ORDER_NAME , ORDER_PWD , ORDER_TEL , ORDER_PHONE , ORDER_POST_NUM , ORDER_ADDR , ORDER_ADDR_DETAIL , ORDER_EMAIL , RECEIVE_NAME , RECEIVE_TEL , RECEIVE_PHONE , RECEIVE_POST_NUM , RECEIVE_ADDR , RECEIVE_ADDR_DETAIL , DELIV_TIME , DELIV_KIND , PACKAGING , COMMON_PWD , BIGO , ORDER_PAY_DT , REG_USER , REG_DT ,TOTAL_PRODUCT_AMT + TOTAL_DELY_AMT + (CASE WHEN DELIV_KIND = 2000 THEN 4000 ELSE 0 END) + (CASE WHEN PACKAGING = 1000 THEN 5000 ELSE 0 END) AS TOTAL_AMOUNT FROM ORDER_MST WHERE ORDER_NO = #{orderNo}
AND USER_NO = #{userNo}
AND ORDER_STATUS = #{orderStatus}
AND USER_SESSION = #{userSession}

mapper.update:
/* OrderMapper.updateOrderApplyInfo */
UPDATE ORDER_MST SET
	  ORDER_NAME		= #{orderName}
	, ORDER_PWD			= #{orderPwd}	
	, ORDER_TEL			= #{orderTel}
	, ORDER_PHONE		= #{orderPhone}
	, ORDER_POST_NUM	= #{orderPostNum}
	, ORDER_ADDR		= #{orderAddr}
	, ORDER_ADDR_DETAIL	= #{orderAddrDetail}
	, ORDER_EMAIL		= #{orderEmail}
	, RECEIVE_NAME		= #{receiveName}
	, RECEIVE_TEL		= #{receiveTel}
	, RECEIVE_PHONE		= #{receivePhone}
	, RECEIVE_POST_NUM	= #{receivePostNum}
	, RECEIVE_ADDR		= #{receiveAddr}
	, RECEIVE_ADDR_DETAIL = #{receiveAddrDetail}
	, DELIV_TIME 		= #{delivTime}
	, DELIV_KIND 		= #{delivKind}
	, PACKAGING 		= #{packaging}
	, COMMON_PWD		= #{commonPwd}
	, BIGO				= #{bigo}
	, UPT_USER			= #{userNo}
	, UPT_DT			= NOW()
WHERE USER_NO = #{userNo}
AND ORDER_NO = #{orderNo}
AND ORDER_STATUS = #{orderStatus}

	AND USER_SESSION = #{userSession}

/* OrderMapper.updateOrderStatusComplete */
UPDATE ORDER_MST SET
	ORDER_STATUS = '0000'
	, UPT_USER			= #{userNo}
	, UPT_DT			= NOW()
	, ORDER_PAY_DT		= NOW()
WHERE ORDER_NO = #{orderNo}
AND ORDER_STATUS = #{orderStatus}

mapper.insert:
/* OrderMapper.insertDelivery */
INSERT INTO DELIVERY_MST(
	  ORDER_NO, ORDER_PRODUCT_NO, MENU_DAY, STATUS, MANAGER_NO
	, DELIVERY_DT, REG_USER, REG_DT, RECEIVE_NAME ,RECEIVE_TEL 
	, RECEIVE_PHONE ,RECEIVE_POST_NUM ,RECEIVE_ADDR ,RECEIVE_ADDR_DETAIL
)
VALUES(
	  #{orderNo}
	, #{orderProductNo}
	, #{menuDay}
	, '1000'
	, null
	, null
	, #{receiveName}
	, NOW()
	, #{receiveName}
	, #{receiveTel}
	, #{receivePhone}
	, #{receivePostNum}
	, #{receiveAddr}
	, #{receiveAddrDetail}
)
