mapper.insert:
/* OrderMapper.insertOrderMst */

	SELECT FN_CREATE_ORDER_NO() AS ORDER_NO

INSERT INTO ORDER_MST
	(
			ORDER_NO
		,	USER_NO
		,	ORDER_STATUS
		,	USER_SESSION
		,	ORDER_PAY_TYPE
		,	TOTAL_PRODUCT_AMT
		,	TOTAL_DIS_AMT
		,	TOTAL_DELY_AMT
		,	MARKETING_AGREE_FG
		,	REG_USER
		,	REG_DT
	)
VALUES(
			#{orderNo}
		,	#{userNo}
		,	'1000'
		,	#{userSession}
		,	'1'
		,	#{totalProductAmt}
		,	#{totalDisAmt}
		,	#{totalDelyAmt}
		,	'Y'
		,	#{userNo}
		,	NOW()
)

/* OrderMapper.insertOrderProduct */
INSERT INTO ORDER_PRODUCT(ORDER_NO, ORDER_QTY, MENU_BOARD_SEQ, MENU_DAY, MENU_MSG, PAY_AMT, DELIVERY_TYPE, USED_POINT, REG_USER, REG_DT)

SELECT	  #{orderNo}
		, #{orderQty}
		, A.MENU_BOARD_SEQ
		, A.MENU_DAY
		, A.MENU_MSG
		, IFNULL(#{payAmt}, 0)
		, 'Y'
		, IFNULL(#{usedPoint}, 0)
		, #{regUser}
		, NOW()
FROM MENU_BOARD A
WHERE A.MENU_BOARD_SEQ = #{menuBoardSeq}

	SELECT LAST_INSERT_ID()

mapper.update:
/* OrderMapper.updateOrderApplyInfo */
UPDATE ORDER_MST SET
	  ORDER_NAME		= #{orderName}
	, ORDER_PWD			= #{orderPwd}	
	, ORDER_TEL			= #{orderTel}
	, ORDER_PHONE		= #{orderPhone}
	, ORDER_POST_NUM	= #{orderPostNum}
	, ORDER_ADDR		= #{orderAddr}
	, ORDER_ADDR_DETAIL	= #{orderAddrDetail}
	, ORDER_EMAIL		= #{orderEmail}
	, RECEIVE_NAME		= #{receiveName}
	, RECEIVE_TEL		= #{receiveTel}
	, RECEIVE_PHONE		= #{receivePhone}
	, RECEIVE_POST_NUM	= #{receivePostNum}
	, RECEIVE_ADDR		= #{receiveAddr}
	, RECEIVE_ADDR_DETAIL = #{receiveAddrDetail}
	, COMMON_PWD		= #{commonPwd}
	, BIGO				= #{bigo}
	, UPT_USER			= #{userNo}
	, UPT_DT			= SYSDATE()
WHERE USER_NO = #{userNo}
AND ORDER_NO = #{orderNo}
AND ORDER_STATUS = #{orderStatus}

	AND USER_SESSION = #{userSession}
