mapper.select:
/* AdmOrderMapper.selectOrderApplyList */ SELECT T.* FROM ( SELECT A.ORDER_NO , A.USER_NO , A.ORDER_STATUS , A.USER_SESSION , A.ORDER_PAY_TYPE , A.TOTAL_PRODUCT_AMT , A.TOTAL_DIS_AMT , A.TOTAL_DELY_AMT , A.MARKETING_AGREE_FG , A.ORDER_NAME , A.ORDER_PWD , A.ORDER_TEL , A.ORDER_PHONE , A.ORDER_POST_NUM , A.ORDER_ADDR , A.ORDER_ADDR_DETAIL , A.ORDER_EMAIL , A.RECEIVE_NAME , A.RECEIVE_TEL , A.RECEIVE_PHONE , A.RECEIVE_POST_NUM , A.RECEIVE_ADDR , A.RECEIVE_ADDR_DETAIL , A.COMMON_PWD , DATE_FORMAT(A.ORDER_PAY_DT,'%Y-%m-%d %H:%i:%s') AS ORDER_PAY_DT , A.BIGO , A.REG_USER , DATE_FORMAT(A.REG_DT,'%Y-%m-%d %H:%i:%s') AS REG_DT , C.PAY_METHOD , C.ORDER_NAME AS PRODUCT_NAME , C.TOTAL_AMOUNT , B.STATUS as DELIVERY_STATUS , CASE WHEN B.STATUS = '1000' THEN '배송전' WHEN B.STATUS = '0000' THEN '배송완료' ELSE '배송전' END AS DELIVERY_STATUS_NM , B.MANAGER_NO , B.DELIVERY_DT FROM ORDER_MST A LEFT JOIN DELIVERY_MST B ON A.ORDER_NO = B.ORDER_NO LEFT JOIN PAYMENTS C ON A.ORDER_NO = C.ORDER_ID WHERE A.ORDER_STATUS != '1000'
AND DATE_FORMAT(A.ORDER_PAY_DT,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND A.ORDER_STATUS = ${orderStatus}
AND A.ORDER_NAME LIKE CONCAT('%',#{searchText},'%')
AND A.ORDER_NO = #{searchText}
GROUP BY A.ORDER_NO ) T ORDER BY T.REG_DT DESC
LIMIT #{startPage}, #{endPage}

/* AdmOrderMapper.selectOrderApplyListCount */
SELECT 
	COUNT(*) as totalCount
FROM 
	(
		SELECT 
			 1
		FROM ORDER_MST A
		LEFT JOIN DELIVERY_MST B ON A.ORDER_NO = B.ORDER_NO
		WHERE A.ORDER_STATUS != '1000'

			AND DATE_FORMAT(A.ORDER_PAY_DT,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}


			AND A.ORDER_STATUS = ${orderStatus}



				AND A.ORDER_NAME LIKE CONCAT('%',#{searchText},'%')



					AND A.ORDER_NO = #{searchText}



		GROUP BY A.ORDER_NO
	) T

/* AdmOrderMapper.selectOrderApplyExcel */ SELECT T.* FROM ( SELECT A.ORDER_NO , A.USER_NO , A.ORDER_STATUS , A.USER_SESSION , A.ORDER_PAY_TYPE , A.TOTAL_PRODUCT_AMT , A.TOTAL_DIS_AMT , A.TOTAL_DELY_AMT , A.MARKETING_AGREE_FG , A.ORDER_NAME , A.ORDER_PWD , A.ORDER_TEL , A.ORDER_PHONE , A.ORDER_POST_NUM , A.ORDER_ADDR , A.ORDER_ADDR_DETAIL , A.ORDER_EMAIL , A.RECEIVE_NAME , A.RECEIVE_TEL , A.RECEIVE_PHONE , A.RECEIVE_POST_NUM , A.RECEIVE_ADDR , A.RECEIVE_ADDR_DETAIL , A.COMMON_PWD , DATE_FORMAT(A.ORDER_PAY_DT,'%Y-%m-%d %H:%i:%s') AS ORDER_PAY_DT , A.BIGO , A.REG_USER , DATE_FORMAT(A.REG_DT,'%Y-%m-%d %H:%i:%s') AS REG_DT , C.PAY_METHOD , C.ORDER_NAME AS PRODUCT_NAME , C.TOTAL_AMOUNT , B.STATUS as DELIVERY_STATUS , CASE WHEN B.STATUS = '1000' THEN '배송전' WHEN B.STATUS = '0000' THEN '배송완료' ELSE '배송전' END AS DELIVERY_STATUS_NM , B.MANAGER_NO , B.DELIVERY_DT FROM ORDER_MST A LEFT JOIN DELIVERY_MST B ON A.ORDER_NO = B.ORDER_NO LEFT JOIN PAYMENTS C ON A.ORDER_NO = C.ORDER_ID WHERE A.ORDER_STATUS != '1000'
AND DATE_FORMAT(A.ORDER_PAY_DT,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND A.ORDER_STATUS = ${orderStatus}
AND A.ORDER_NAME LIKE CONCAT('%',#{searchText},'%')
AND A.ORDER_NO = #{searchText}
GROUP BY A.ORDER_NO ) T ORDER BY T.REG_DT DESC

/* OrderMapper.selectDeliveryList */ SELECT A.DELIVERY_SEQ , A.ORDER_NO , A.ORDER_PRODUCT_NO , DATE_FORMAT(A.MENU_DAY,'%Y년 %m월 %d일') AS MENU_DAY , A.STATUS as DELIVERY_STATUS , CASE WHEN A.STATUS = '1000' THEN '배송전' WHEN A.STATUS = '0000' THEN '배송완료' ELSE '배송전' END AS DELIVERY_STATUS_NM , IFNULL((SELECT MANAGER_NM FROM MANAGER_MST MM WHERE MM.MANAGER_NO = A.MANAGER_NO),'미배정') AS MANAGER_NM , IFNULL(DATE_FORMAT(A.DELIVERY_DT,'%Y-%m-%d %H:%i:%s'),'') AS DELIVERY_DT , IFNULL(A.RECEIVE_NAME,'') AS RECEIVE_NAME , IFNULL(A.RECEIVE_TEL,'') AS RECEIVE_TEL , IFNULL(A.RECEIVE_PHONE,'') AS RECEIVE_PHONE , IFNULL(A.RECEIVE_POST_NUM,'') AS RECEIVE_POST_NUM , IFNULL(A.RECEIVE_ADDR,'') AS RECEIVE_ADDR , IFNULL(A.RECEIVE_ADDR_DETAIL,'') AS RECEIVE_ADDR_DETAIL , DATE_FORMAT(A.REG_DT,'%Y-%m-%d %H:%i:%s') AS REG_DT , B.ORDER_QTY FROM DELIVERY_MST A JOIN ORDER_PRODUCT B ON A.ORDER_PRODUCT_NO = B.ORDER_PRODUCT_NO AND A.ORDER_NO = B.ORDER_NO WHERE 1=1
AND DATE_FORMAT(A.REG_DT,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND A.STATUS = ${delivStatus}
AND A.RECEIVE_NAME LIKE CONCAT('%',#{searchText},'%')
AND A.ORDER_NO = #{searchText}
ORDER BY
MENU_DAY
REG_DT
DESC
ASC
LIMIT #{startPage}, #{endPage}

/* OrderMapper.selectDeliveryListCount */
SELECT
	COUNT(*) as totalCount
FROM DELIVERY_MST A
WHERE 1=1

	AND DATE_FORMAT(A.REG_DT,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}


	AND A.STATUS = ${delivStatus}



		AND A.RECEIVE_NAME LIKE CONCAT('%',#{searchText},'%')



			AND A.ORDER_NO = #{searchText}



ORDER BY REG_DT DESC
