mapper.select:
/* AdmOrderMapper.selectOrderApplyList */ SELECT T.* FROM ( SELECT A.ORDER_NO , A.USER_NO , A.ORDER_STATUS , A.USER_SESSION , A.ORDER_PAY_TYPE , A.TOTAL_PRODUCT_AMT , A.TOTAL_DIS_AMT , A.TOTAL_DELY_AMT , A.MARKETING_AGREE_FG , A.ORDER_NAME , A.ORDER_PWD , A.ORDER_TEL , A.ORDER_PHONE , A.ORDER_POST_NUM , A.ORDER_ADDR , A.ORDER_ADDR_DETAIL , A.ORDER_EMAIL , A.RECEIVE_NAME , A.RECEIVE_TEL , A.RECEIVE_PHONE , A.RECEIVE_POST_NUM , A.RECEIVE_ADDR , A.RECEIVE_ADDR_DETAIL , A.COMMON_PWD , DATE_FORMAT(A.ORDER_PAY_DT,'%Y-%m-%d %H:%i:%s') AS ORDER_PAY_DT , A.BIGO , A.REG_USER , A.REG_DT FROM ORDER_MST A WHERE A.ORDER_STATUS != '1000'
AND DATE_FORMAT(A.ORDER_PAY_DT,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND A.ORDER_STATUS = ${orderStatus}
AND A.ORDER_NAME LIKE CONCAT('%',#{searchText},'%')
AND A.ORDER_NAME = #{searchText}
) T ORDER BY T.REG_DT DESC LIMIT #{startPage}, #{endPage}

/* AdmOrderMapper.selectOrderApplyListCount */ SELECT COUNT(*) as totalCount FROM ( SELECT A.ORDER_NO , A.USER_NO , A.ORDER_STATUS , A.USER_SESSION , A.ORDER_PAY_TYPE , A.TOTAL_PRODUCT_AMT , A.TOTAL_DIS_AMT , A.TOTAL_DELY_AMT , A.MARKETING_AGREE_FG , A.ORDER_NAME , A.ORDER_PWD , A.ORDER_TEL , A.ORDER_PHONE , A.ORDER_POST_NUM , A.ORDER_ADDR , A.ORDER_ADDR_DETAIL , A.ORDER_EMAIL , A.RECEIVE_NAME , A.RECEIVE_TEL , A.RECEIVE_PHONE , A.RECEIVE_POST_NUM , A.RECEIVE_ADDR , A.RECEIVE_ADDR_DETAIL , A.COMMON_PWD , DATE_FORMAT(A.ORDER_PAY_DT,'%Y-%m-%d %H:%i:%s') AS ORDER_PAY_DT , A.BIGO , A.REG_USER , A.REG_DT FROM ORDER_MST A WHERE A.ORDER_STATUS != '1000'
AND DATE_FORMAT(A.ORDER_PAY_DT,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND A.ORDER_STATUS = ${orderStatus}
AND A.ORDER_NAME LIKE CONCAT('%',#{searchText},'%')
AND A.ORDER_NAME = #{searchText}
) T ORDER BY T.REG_DT DESC
